// Utilities
import {
  mapGetters,
  mapMutations
} from 'vuex'

export default {
  name: 'CoreDrawer',

  computed: {
    ...mapGetters(['links']),
    drawer: {
      get () {
        return this.$store.state.drawer
      },
      set (val) {
        this.setDrawer(val)
      }
    }
  },

  methods: {
    ...mapMutations(['setDrawer']),
    onClick (e, item) {
      e.stopPropagation()

      if (item.to === '/') {
        this.$vuetify.goTo(0)
        this.setDrawer(false)
        return
      }

      if (item.to || !item.href) return

      this.$vuetify.goTo(item.href)
      this.setDrawer(false)
    }
  }
}
